mapper.select:
select
		driver.id,driver.name, driver.chip_code as chipCode, driver.engineer_id as engineerId 
		from driver where driver.id=#{id}

select driver.id,driver.name,product.code as productCode,driver.chip_code as chipCode, driver.engineer_id as engineerId, 
		engineer.name as engineerName, product.customer_id as customerId, customer.name as customerName,newVersion.lastVersion
		from driver left join ((select DRIVER_ID,max(version) as lastVersion from version group by DRIVER_ID) as newVersion,product,user as customer) 
		on (driver.ID = newVersion.DRIVER_ID and driver.PRODUCT_ID = product.ID and product.customer_id = customer.id),user as engineer
		where driver.engineer_id = engineer.id limit #{page.limit} offset #{page.offset}
