mapper.select:
select
		version.id,version.version,version.sequence,version.file_name as fileName, version.remark, version.add_time as addTime, version.driver_id as driverId, version.burn_sum as burnSum
		from version where version.id=#{id}

select
		version.id,version.version,version.sequence,version.file_name as fileName, version.remark, version.add_time as addTime, version.driver_id as driverId, version.burn_sum as burnSum
		from version,driver,product where version.id=#{versionId} and version.DRIVER_ID = driver.ID and driver.PRODUCT_ID = product.ID and product.CUSTOMER_ID = #{customerId}

select
		version.id,version.version,version.sequence,version.file_name as fileName, version.remark, version.add_time as addTime, version.driver_id as driverId, version.burn_sum as burnSum
		from version where version.driver_id=#{driverId}

select
		version.id,version.version,version.sequence,version.file_name as fileName, version.remark, version.add_time as addTime, version.driver_id as driverId, version.burn_sum as burnSum
		from version
		where version.driver_id=#{driverId}
		limit #{page.limit} offset #{page.offset}
