mapper.select:
select
		driver.id, driver.name, driver.chip_code as chipCode, driver.engineer_id as engineerId, engineer.name as engineerName, product.code as productCode
		from driver left join (user as engineer, product) on (driver.engineer_id = engineer.id and driver.product_id = product.id)where driver.id=#{id}

select driver.id,driver.name,product.code as productCode,driver.chip_code as chipCode, driver.engineer_id as engineerId, engineer.name as engineerName, product.customer_id as customerId, customer.name as customerName,newVersion.lastVersion from driver left join product ON driver.PRODUCT_ID = product.ID left join user as customer ON product.customer_id = customer.id left join user as engineer ON driver.engineer_id = engineer.id left join (select DRIVER_ID, max(version) as lastVersion from version group by DRIVER_ID) as newVersion ON driver.ID = newVersion.DRIVER_ID
driver.name like '%${driver.name}%' and product.customer_id = #{driver.customerId} and driver.engineer_id = #{driver.engineerId}
limit #{page.limit} offset #{page.offset}

select count(*) from driver left join product on driver.PRODUCT_ID = product.ID

	driver.name like '%${driver.name}%'
	and product.customer_id = #{driver.customerId}
