mapper.select:
select user.id,user.name,user.type,user.password,user.account,user_type.name as typeName from user,user_type
user.type = #{user.type}
and user.name like '%${user.name}%'
and user.type = user_type.id
limit #{page.limit} offset #{page.offset}

select count(*) from user


		user.type = #{user.type}


		and user.name like '%${user.name}%'
